import React, {useState} from "react"
import Avatar from "react-avatar-edit"
import { Dialog } from 'primereact/dialog'
import {Button} from 'primereact/button'
import { InputText } from 'primereact/inputtext'
import img from './profile.jpeg'



const Profile = () => {
    const [image, setimage] = useState("");
    const [imagecrop, setimagecrop]=useState("");
    const [src, setsrc] = useState(false);
    const [profile, setprofile] = useState([]);
    const [pview, setpview] = useState(false);

    const profileFinal = profile.map((item) => item.preview);

    const onClose = () => {
        setpview(null)
    };

    const onCrop = (view) => {
        setpview(view)
    };

    const saveCropImage = () => {
        setprofile([...profile, {pview}]);
        setimagecrop(false);
    };
    return(
        <div className="profile-img">
        <div className="dialog">
            <img
            style={{
                width: "200px",
                height: "200px",
                borderRadius: "50%",
                objectFit: "cover",
                border: "4px solid green",
            }}
            onClick={()=> setimagecrop(true)}
            src={profileFinal.length? profileFinal: img} alt=""/>
            <label htmlFor="" className="mt 3 font-semibold text-5xl">Faith Moraa</label>
            <Dialog
                visible={imagecrop}
                header={()=>(
                    <p htmlFor="" className="text-2xl font-semibold textColor">
                        Update Profile
                    </p>
                )}
                onHide={()=> setimagecrop(false)}

            >
                <div className="confirmation-content flex flex-column align-items-centre">
                    <Avatar
                      width={500}
                      height={400}
                      onCrop={onCrop}
                      onClo                    

                    />

                </div>
            </Dialog>

            <InputText 
            type="file"
            accept="image/*"
            onChange={(event)=>{
                const file = event.target.files[0];
                if(file && file.type.substring(0, 5)==="image"){
                    setimage(file);
                }else{
                    setimage(null);
                }
            }}

            />
        </div>
        </div>
    )

}

export default Profile
