import React, { useState } from "react"
import * as Components from './Components'
import './login2.css'
import {Link} from 'react-router-dom'



function Login2({setToken}) {

    const [loggedIn, setLoggedIn] = useState(false);
    const [username, setUsername] = useState('')
    const [signIn, toggle] = React.useState(true);
    const [password, setPassword] = useState('')
    const [email, setEmail] = useState('')

    function handleUsername (e) {
        e.preventDefault()
        setUsername(e.target.value)
    }

    function handlePassword (e) {
        e.preventDefault()
        setPassword(e.target.value)
    }

    function handleEmail (e) {
        e.preventDefault()
        setEmail(e.target.value)
    }

    function handleSignUp(e) {
        e.preventDefault();
        const token = {
          username,
          password,
          email
        };
        setToken(token);
      }

      function handleSignIn(e) {
        e.preventDefault();
        const token = {
          email,
          password
        };
        setToken(token);
      }


    return(
        <div>


        <Components.Container>
            <Components.SignUpContainer signinIn={signIn}>
                <Components.Form onSubmit={handleSignUp}>
                    <Components.Title>Create Account</Components.Title>
                    <Components.Input type='text' placeholder='Name' onChange={handleUsername}/>
                    <Components.Input type='email' placeholder='Email' onChange={handleEmail}/>
                    <Components.Input type='password' placeholder='Password' onChange={handlePassword}/>
                    <Link to="/register">
                        <Components.Button>Sign Up</Components.Button>
                    </Link>                   
                </Components.Form>
            </Components.SignUpContainer>

            <Components.SignInContainer signinIn={signIn}>
                 <Components.Form onSubmit={handleSignIn}>
                     <Components.Title>Sign in</Components.Title>
                     <Components.Input type='email' placeholder='Email' onChange={handleEmail} />
                     <Components.Input type='password' placeholder='Password' onChange={handlePassword}/>
                     <Components.Anchor href='#'>Forgot your password?</Components.Anchor>
                     <Link to="/bio">
                        <Components.Button>Sign In</Components.Button>
                    </Link>
                 </Components.Form>
            </Components.SignInContainer>

            <Components.OverlayContainer signinIn={signIn}>
                <Components.Overlay signinIn={signIn}>

                <Components.LeftOverlayPanel signinIn={signIn}>
                    <Components.Title>Welcome Back!</Components.Title>
                    <Components.Paragraph>
                        To keep connected with us please login with your personal info
                    </Components.Paragraph>
                    <Components.GhostButton onClick={() => toggle(true)}>
                        Sign In
                    </Components.GhostButton>
                    </Components.LeftOverlayPanel>

                    <Components.RightOverlayPanel signinIn={signIn}>
                      <Components.Title>Hello, Friend!</Components.Title>
                      <Components.Paragraph>
                          Enter Your personal details and start journey with us
                      </Components.Paragraph>
                          <Components.GhostButton onClick={() => toggle(false)}>
                              Sign Up
                          </Components.GhostButton> 
                    </Components.RightOverlayPanel>

                </Components.Overlay>
            </Components.OverlayContainer>

        </Components.Container>

        </div>
    )
}

export default Login2
