import React,{useState} from 'react';
import './App.css'
import ProfilePage from '../my-profile/profile';
import Login2 from '../login2/login2'
import Registration from '../registration/registration';
import Navbar from '../navbar/navbar';
import {Route, Routes} from "react-router-dom"




function App() {

	// states to change
	const [username, setUsername] = useState('');
	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');

	// sets the id 
	const [logedUserId, setLogedUserId] =useState(0)

	// sets if the login is succesfull or not

	const [allow, setAllow] = useState("")
	const [failedLogInMsg, setFailedLogInMsg] =  useState("noMsg")

	// sets a users skills
	const [skills, setSkills] = useState([])


		let token = {
			name: username,
			email: email,
			password_hash: password,
		};

		let loginToken = {
			email: email,
			password_hash: password,
		};






	function handleLogIn(){
		fetch('http://127.0.0.1:9292/signin', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify(loginToken),
		})
			.then((res) => res.json())
			.then((data) => {
				console.log(data);
				setAllow(data.message)
				setLogedUserId(data.id)
				console.log(logedUserId)
				setUsername(data.name)

				// gotProjects(data.id)


			});
	}

	const handleSignUp = () => {
		fetch('http://127.0.0.1:9292/signup', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify(token),
		})
		.then(res => res.json())
		.then((data) => {
			console.log(data);
			setAllow(data.message)
			setLogedUserId(data.id)

		})

	};

  return (
   <>

    <div className="nav-container">
    <Navbar  />
      <Routes>
        <Route path="/" element={<Login2/>} />
        <Route path="/bio" element={<ProfilePage/>} />
        <Route path="/register" element={<Registration />} />
        <Route path="/logout" element={<Login2 />} />
      </Routes>

    </div>
   </>

  );

}

export default App;
